class PLTPageTests(unittest.TestCase):

    DUMP_FILE_CONTENT1 = ""
    DUMP_FILE_CONTENT2 = ""

    
    defaultMap = dict()

    @classmethod
    def setUpClass(self):
        self.defaultMap['pltInfo'] = {2: 2}

    def test_contructor(self):
        pltPage = PLTPage.PLTPage({2: 2})
        self.assertEqual(self.defaultMap, pltPage.toMap())

    def test_contructor2(self):
        self.assertRaises(ValueError, PLTPage.PLTPage, 64)

    def test_toMap(self):
        pltPage = PLTPage.PLTPage({2: 2})
        map = pltPage.toMap()

        self.assertEqual(self.defaultMap, map)

    @unittest.mock.patch('myCode.PLTPage.getListOfProcesses')
    @unittest.mock.patch('myCode.PLTEntry.getProcessusPages')
    @unittest.mock.patch('myCode.PLTEntry.removePLTEntryDataExtraZeros')
    def test_getDictionary(self, mock_utils, mock_utils2, mock_utils3):
        hex = "82542a00000000000000000000000000"
        mock_utils.return_value = {1, 2, 3}
        mock_utils2.return_value = PLTEntry.PLTEntry([2])
        mock_utils3.return_value = "2"
        pltPage = PLTPage.getDictionary(self.DUMP_FILE_CONTENT1)
        self.assertEqual(pltPage.toMap().get("pltInfo"), {'2': [2]})

