import unittest
from myCode import PTEntry
from myCode import PTPage
from myCode import MyUtils
from myCode.CustomExceptions import *


class PTPageTests(unittest.TestCase):

    DUMP_FILE_CONTENT1 = ""
    defaultMap = dict()

    @classmethod
    def setUpClass(self):
        self.defaultMap['ptinfo'] = {2: 2}

    def test_contructor(self):
        ptPage = PTPage.PTPage({2: 2})
        self.assertEqual(self.defaultMap, ptPage.toMap())
        self.assertEqual({2: 2}, ptPage.getPagesList())

    def test_contructor2(self):
        self.assertRaises(ValueError, PTPage.PTPage, 64)

    def test_toMap(self):
        ptPage = PTPage.PTPage({2: 2})
        map = ptPage.toMap()

        self.assertEqual(self.defaultMap, map)

    @unittest.mock.patch('myCode.PTEntry.parseFromHexDump')
    def test_parseFromHex6(self, mock_utils):
        hex = "FFE0000800200000000008000C000000"
        entry = PTEntry.PTEntry([0, 3], False, 84, 84)
        
        mock_utils.return_value = PTEntry.PTEntry([0, 3], False, 84, 84)
        ptPage = PTPage.getDictionary(self.DUMP_FILE_CONTENT1, hex)
        self.assertDictContainsSubset(ptPage.toMap().get('ptinfo').get(0), entry.toMap())


    if __name__ == "__main__":
        unittest.main()
