import unittest
import unittest.mock

from myCode import PageBroker


class PageBrokerTests(unittest.TestCase):

	DUMP_FILE_CONTENT1 = ""

	DUMP_FILE_CONTENT2 = ""

	@unittest.mock.patch('myCode.PageBroker.MyUtils')
	def test_getPage(self, mock_utils):
		mock_utils.readBlockFromFileInHex.side_effect = [self.DUMP_FILE_CONTENT1[0:256], self.DUMP_FILE_CONTENT1]

		#mock_utils.readBlockFromFileInHex.return_value = self.DUMP_FILE_CONTENT1
		actual = PageBroker.getPage(0)
		expectedStart = "4D4C4D4C56534D4C001000400000000100020001" #len = 40
		self.assertTrue(actual.startswith(expectedStart))

		while len(expectedStart) < 4096:
			expectedStart = expectedStart + "00"

		self.assertEqual(expectedStart, actual)

	@unittest.mock.patch('myCode.PageBroker.MyUtils')
	def test_getPage2(self, mock_utils):
		mock_utils.readBlockFromFileInHex.side_effect = [self.DUMP_FILE_CONTENT1[0:256], self.DUMP_FILE_CONTENT2]
		actual = PageBroker.getPage(1)
		expectedStart = "FA1086008000000000000000"
		self.assertTrue(actual.startswith(expectedStart))

		while len(expectedStart) < 4096:
			expectedStart = expectedStart + "00"

		self.assertEqual(expectedStart, actual)

